import React, { useEffect, useState } from 'react';
import { getShardingStatus, getResults, resetSharding } from '../services/api';
import ResultView from './ResultView';

function MonitorView() {
  const [status, setStatus] = useState('Idle');
  const [message, setMessage] = useState('');
  const [elapsedTime, setElapsedTime] = useState(0);
  const [results, setResults] = useState(null);
  const [shardingStatusFetched, setShardingStatusFetched] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await getShardingStatus();
        setStatus(response.data.status);
        setMessage(response.data.message);

        if (response.data.status === 'In Progress') {
          setElapsedTime(response.data.elapsed_time);
        } else if (response.data.status === 'Completed') {
          try {
            const resultResponse = await getResults();
            setResults(resultResponse.data);
          } catch (error) {
            console.error('Failed to fetch results:', error);
          }
          setShardingStatusFetched(true); // Set to true after both fetches succeed
        }
      } catch (error) {
        console.error('Error fetching sharding status:', error);
      }
    };

    const interval = setInterval(fetchData, 100);
    return () => clearInterval(interval);
  }, [shardingStatusFetched]); // Depend on shardingStatusFetched to control polling

  const handleClear = () => {
    // Reset the frontend state to its initial values
    setStatus('Idle');
    setMessage('');
    setElapsedTime(0);
    setResults(null);
    setShardingStatusFetched(false); // Reset the flag to allow fetching sharding status again

    // Clear sessionStorage to reset data
    sessionStorage.clear();

    // Optionally call the backend to reset any server-side state
    resetSharding()
      .then(() => {
        console.log('Sharding reset successfully');
      })
      .catch((error) => {
        console.error('Failed to reset sharding:', error);
      });
  };

  return (
    <section>
      <h2>Sharding Status</h2>
      <p><strong>Status:</strong> {status}</p>
      <p><strong>Message:</strong> {message}</p>
      {/* If sharding is in progress, show elapsed time */}
      {status === 'In Progress' && (
        <p><strong>Elapsed Time:</strong> {elapsedTime} seconds</p>
      )}

      <h2>Sharding Results</h2>
      {/* If results are available, show them */}
      {results ? (
        <ResultView metadata={results} /> // Pass results as props to ResultView
      ) : (
        <p>Waiting for sharding to complete...</p>
      )}

      {/* Clear button to reset the process */}
      <button onClick={handleClear}>Clear</button>
    </section>
  );
}

export default MonitorView;
