import React, { useEffect, useState } from 'react';
import { getShardingStatus, getResults, resetSharding } from '../services/api';
import ResultView from './ResultView';

function MonitorView() {
  const [status, setStatus] = useState('Idle');
  const [message, setMessage] = useState('');
  const [elapsedTime, setElapsedTime] = useState(0); // Track elapsed time
  const [results, setResults] = useState(null); // Store the results after sharding is complete
  const [shardingStatusFetched, setShardingStatusFetched] = useState(false); // Track if sharding status has been fetched

  useEffect(() => {
    // Only fetch sharding status if it hasn't been fetched yet and the status is not already "Completed"
    if (!shardingStatusFetched && status !== 'Completed') {
      const interval = setInterval(() => {
        getShardingStatus()
          .then((response) => {
            setStatus(response.data.status);
            setMessage(response.data.message);

            // If sharding is in progress, update elapsed time
            if (response.data.status === 'In Progress') {
              setElapsedTime(response.data.elapsed_time);
            }

            // If sharding is complete, stop the polling and fetch the results
            if (response.data.status === 'Completed') {
              clearInterval(interval);
              // Fetch the results once sharding is complete
              getResults()
                .then((resultResponse) => {
                  setResults(resultResponse.data); // Update the results
                })
                .catch((error) => {
                  console.error('Failed to fetch results:', error);
                });
              setShardingStatusFetched(true); // Set flag to prevent future calls
            }
          })
          .catch((error) => {
            console.error('Failed to fetch sharding status:', error);
          });
      }, 2000); // Poll every 2 seconds

      return () => clearInterval(interval); // Cleanup on component unmount
    }
  }, [shardingStatusFetched, status]); // Trigger this effect when shardingStatusFetched or status changes

  const handleClear = () => {
    // Reset the frontend state to its initial values
    setStatus('Idle');
    setMessage('');
    setElapsedTime(0);
    setResults(null);
    setShardingStatusFetched(false); // Reset the flag to allow fetching sharding status again

    // Optionally call the backend to reset any server-side state
    resetSharding()
      .then(() => {
        console.log('Sharding reset successfully');
      })
      .catch((error) => {
        console.error('Failed to reset sharding:', error);
      });
  };

  return (
    <section>
      <h2>Sharding Status</h2>
      <p><strong>Status:</strong> {status}</p>
      <p><strong>Message:</strong> {message}</p>
      {/* If sharding is in progress, show elapsed time */}
      {status === 'In Progress' && (
        <p><strong>Elapsed Time:</strong> {elapsedTime} seconds</p>
      )}

      <h2>Sharding Results</h2>
      {/* If results are available, show them */}
      {results ? (
        <ResultView metadata={results} /> // Pass results as props to ResultView
      ) : (
        <p>Waiting for sharding to complete...</p>
      )}

      {/* Clear button to reset the process */}
      <button onClick={handleClear}>Clear</button>
    </section>
  );
}

export default MonitorView;
