import React, { useEffect, useState } from 'react';
import { getShardingStatus, getResults, resetSharding } from '../services/api';
import ResultView from './ResultView';

function MonitorView() {
  const [status, setStatus] = useState('Idle');
  const [message, setMessage] = useState('');
  const [elapsedTime, setElapsedTime] = useState(0);
  const [results, setResults] = useState(null);

  useEffect(() => {
    let interval = null;

    // Fetch Status Initially (Once on Mount)
    const fetchInitialStatus = async () => {
      try {
        const response = await getShardingStatus();
        setStatus(response.data.status);
        setMessage(response.data.message);

        if (response.data.status === 'In Progress') {
          setElapsedTime(response.data.elapsed_time);
        } else if (response.data.status === 'Completed') {
          const resultResponse = await getResults();
          setResults(resultResponse.data);
        }
      } catch (error) {
        console.error('Error during initial status fetch:', error);
      }
    };

    // Polling Logic
    const fetchData = async () => {
      try {
        const response = await getShardingStatus();
        setStatus(response.data.status);
        setMessage(response.data.message);

        if (response.data.status === 'In Progress') {
          setElapsedTime(response.data.elapsed_time);
        } else if (response.data.status === 'Completed') {
          clearInterval(interval); // Stop polling
          const resultResponse = await getResults();
          setResults(resultResponse.data);
        }
      } catch (error) {
        console.error('Error fetching sharding status:', error);
      }
    };

    // Run Initial Status Check
    fetchInitialStatus();

    // Start Polling Only If In Progress
    if (status === 'In Progress') {
      interval = setInterval(fetchData, 2000); // Poll every 2 seconds
    }

    return () => {
      if (interval) clearInterval(interval); // Cleanup on unmount
    };
  }, [status]); // Trigger when status changes

  const handleClear = () => {
    setStatus('Idle');
    setMessage('');
    setElapsedTime(0);
    setResults(null);

    sessionStorage.clear();

    resetSharding()
      .then(() => {
        console.log('Sharding reset successfully');
      })
      .catch((error) => {
        console.error('Failed to reset sharding:', error);
      });
  };

  return (
    <section>
      <h2>Sharding Status</h2>
      <p><strong>Status:</strong> {status}</p>
      <p><strong>Message:</strong> {message}</p>
      {status === 'In Progress' && (
        <p><strong>Elapsed Time:</strong> {elapsedTime} seconds</p>
      )}

      <h2>Sharding Results</h2>
      {results ? (
        <ResultView metadata={results} />
      ) : (
        <p>Waiting for sharding to complete...</p>
      )}

      <button onClick={handleClear}>Clear</button>
    </section>
  );
}

export default MonitorView;
